section "game/modules/tome/data/talents/chronomancy/induced-phenomena.lua"

t("Epoch", "에폭", "talent name")
t("%s resists the blindness!", "%s 실명에 저항했다!!", "logSeen", nil, {"는"})
t("%s resists the pin!", "%s 속박에 저항했다!", "logSeen", nil, {"가"})
