section "game/modules/tome/data/talents/misc/tutorial.lua"

t("Tutorial-specific talents.", "튜토리얼 한정 기술들.", "_t")
t("tutorial", "튜토리얼", "_t")
t("Shove", "밀치기", "talent name")
t("%s resists the shove!", "%s 밀치기에 저항했다!", "logSeen", nil, {"가"})
t("Give the target a good old-fashioned shove, knocking it back a square.", "대상을 밀어, 한 칸 밀쳐냅니다.", "tformat")
t("Mana Gale", "마나 돌풍", "talent name")
t("%s is knocked back by the gale!", "%s 돌풍에 밀려났다!", "logSeen", nil, {"가"})
t("%s remains firmly planted in the face of the gale!", "%s 강풍에 불구하고 여전히 단단히 버티고 있다.", "logSeen", nil, {"는"})
t("Conjure up a powerful magical wind, pushing the target back a distance of %d.", "강력한 마법의 바람을 만들어 내, 대상을 %d 칸 밀칩니다.", "tformat")
t("Telekinetic Punt", "염동력 밀치기", "talent name")
t("%s is knocked back by the telekinetic blow!", "%s 염동력 공격에 의해 밀려났다!", "logSeen", nil, {"가"})
t("%s holds its ground!", "%s 꿈쩍도 하지않는다!", "logSeen", nil, {"가"})
t("Knock the target backwards with a powerful telekinetic blow.", "염동력 공격으로 대상을 뒤로 밀친다.", "tformat")
t("Blink", "점멸", "talent name")
t("%s is teleported a short distance!", "%s 짧은 거리를 순간이동했다!", "logSeen", nil, {"가"})
t("%s resists the teleportation!", "%s 순간이동에 저항했다!", "logSeen", nil, {"가"})
t("Attempts to magically teleport a target slightly farther from you.", "확률적으로 대상을 시전자에게서 멀리 순간이동시킵니다.", "tformat")
t("Fear", "공포", "talent name")
t("%s retreats in terror!", "%s 공포로 인해 도망간다!", "logSeen", nil, {"가"})
t("%s shakes off the fear!", "%s 두려움을 떨쳐냈다!", "logSeen", nil, {"가"})
t("Attempts to briefly terrify a target into retreating.", "확률적으로 대상을 공포에 질리게 해 도망가게 만듭니다.", "tformat")
t("Bleed", "출혈", "talent name")
t("Inflicts a 10-turn bleed effect.", "10 턴 동안 출혈을 적용합니다.", "tformat")
t("Confusion", "혼란", "talent name")
t("Use your mental powers to confuse the target for five turns.", "정신력을 사용하여 5 턴 동안 혼란시킵니다.", "tformat")


